class Solution:
    def longestBeautifulSubstring(self, word: str) -> int:

        n = len(word)
        target = 'a'
        i = ans = 0
        flag = False
        while i < n:
            if word[i] == 'a' and flag == False or word[i] == 'a' and target != 'a':
                flag = True
                j = i
                target = 'a'
            if word[i] == target:
                while i < n and word[i] == target:
                    if target == 'u':
                        ans = max(ans, i - j + 1)
                    i += 1
                if target == 'a':
                    target = 'e'
                elif target == 'e':
                    target = 'i'
                elif target == 'i':
                    target = 'o'
                elif target == 'o':
                    target = 'u'
                else:
                    target = 'a'
                    flag = False
            else:
                target = 'a'
                flag = False
                i += 1
        return ans

if __name__ == '__main__':
    sol = Solution()
    x = sol.longestBeautifulSubstring('')
    print(x)

